#!usr/bin/env python3

#Nucleotide Composition.
#determine the unique characters in this sequence
DNA_seq = ''

DNA_seq_unique = set(DNA_seq)
print(DNA_seq_unique)

#iterate over each unique character and count the number found in the sequence and store each count in a dictionary. example: nt_comp['A']=2

DNA_seq = 'AAATTTGGCC'
nt_dict = {}
base_count=0
for base in DNA_seq_unique:
    base_count += DNA_seq.count(base)
    print(base_count)
    nt_dict[base] = base_count
    base_count=0

#when you are done counting each character calculate and report the nucleotide composition and the GC content.
print(nt_dict)
print('The GC content of this DNA is', (int(DNA_seq.count('G')) + int(DNA_seq.count('C'))) / len(DNA_seq))
